@import url("https://fonts.googleapis.com/css2?family=Inter:wght@300;400;600&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Inter:wght@300;400;600&display=swap");
body {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

p, h1, h2, h3, h4, h5, h6 {
  margin: 0;
  padding: 0;
}

.contenedor-tarjetas {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
}

.tarjeta {
  width: 200px;
  height: 300px;
  padding: 20px;
  margin: 15px;
  border: 1px solid #FFD33F;
  border-radius: 5px;
  color: #54534f;
  font-family: "Inter", sans-serif;
  background-color: #d6dbd9;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
}

.contenedor-imagen {
  width: 180px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: top;
  flex-wrap: wrap;
}

#imagen-personaje {
  width: 100%;
}

nav {
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
}

.botones-nav {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
}

li {
  padding: 15px;
}

.img-caravan {
  width: 220px;
}

.botones-nav {
  list-style: none;
  font-family: "Inter", sans-serif;
  color: #FFF3CF;
  font-weight: 600;
  font-size: 25px;
}

.seccion-buscador {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
  margin-top: 35px;
  margin-bottom: 35px;
}

label {
  font-family: "Inter", sans-serif;
  color: #FFF3CF;
  font-weight: 600;
  font-size: 25px;
}

body {
  background-color: #54534f;
}

